let newYearsDay=new Date((new Date).getFullYear()+1,0,1),$days=document.getElementById("days"),$hours=document.getElementById("hours"),$minutes=document.getElementById("minutes"),$seconds=document.getElementById("seconds");function updateClock(e){var n=Math.floor(e/86400);e-=86400*n;var t=Math.floor(e/3600)%24;e-=3600*t;var a=Math.floor(e/60)%60;e-=60*a;e=Math.floor(e%60);$days.innerHTML=padNumber(n),$hours.innerHTML=padNumber(t),$minutes.innerHTML=padNumber(a),$seconds.innerHTML=padNumber(e)}function padNumber(e){return e<10?"0"+e:e}setInterval(function(){var e=new Date;updateClock((newYearsDay-e)/1e3)},1e3);